import 'antd/dist/antd.css';
import './index.css';
import { Image, Button, Space, Avatar, Descriptions, Row, Col } from 'antd';
import ReactGoogleMapLoader from "react-google-maps-loader"

import React, { useEffect, useState } from 'react';

const GetUserData = () => {
    const [data, setData] = useState([]);
    const [user, setUser] = useState([]);
    const [loadingData, setLoadingData] = useState(false);

    useEffect(() => {
        fetchdata();
    }, []);


    const fetchdata = async () => {
        const response = await fetch("https://randomuser.me/api");
        const data = await response.json();
        console.log(data.results[0]);
        setData(data.results[0]);
        setLoadingData(true);
        title(data);
    };

    function title(title) {
        document.title = title.results[0].name.first;
    }

    const mapStyles = {
        width: '100%',
        height: '100%'
    };


    return (
        <div>
        {loadingData ? (
                <Row>
                    <Col span={12}>
                        <div><Avatar size={250} src={<Image src={data.picture.large}  style={{ width: 300 }} />} /></div>
                        <div>
                        <Descriptions
                            title={data.name.first  + " Details"}
                            bordered
                            column={{ xxl: 4, xl: 3, lg: 3, md: 3, sm: 2, xs: 1 }}
                            >
                            <Descriptions.Item label="User Name">{data.name.title} {data.name.first} {data.name.last}</Descriptions.Item>
                            <Descriptions.Item label="Gender">{data.gender}</Descriptions.Item>
                            <Descriptions.Item label="DOB">{data.dob.date}</Descriptions.Item>
                            <Descriptions.Item label="Contact">{data.cell}</Descriptions.Item>
                            <Descriptions.Item label="Email"><a href={"mailto:" + data.email}>{data.email}</a></Descriptions.Item>
                            <Descriptions.Item label="Age">{data.dob.age}</Descriptions.Item>
                            <Descriptions.Item label="Address">
                                {data.location.street.name}, {data.location.street.number}
                                <br />
                                {data.location.state}, {data.location.city}
                                <br />
                                {data.location.country} ({data.nat}), {data.location.postalcode}
                            </Descriptions.Item>
                            <Descriptions.Item label="Registration">
                                {data.registered.date}
                            </Descriptions.Item>
                            </Descriptions>
                        </div>
                    </Col>
                    <Col span={12}>
                        <ReactGoogleMapLoader
                                params={{
                                    key: YOUR_API_KEY, // Define your api key here
                                    libraries: "places,geometry", // To request multiple libraries, separate them with a comma
                                }}
                                render={googleMaps => googleMaps && <div>Google Maps is loaded !</div>}
                            />
                    </Col>
                </Row>
        ) : (
          <p>No data</p>
        )}
        <br/>
        <button onClick={() => fetchdata()}>Refresh Data <i className="fa fa-refresh"></i></button>
      </div>
    );
};

export default GetUserData;
