import 'antd/dist/antd.css';
import './index.css';
import { Image, Button, Space, Avatar } from 'antd';

import React, { useEffect, useState } from 'react';

const GetUserData = () => {
    const [data, setData] = useState([]);
    const [user, setUser] = useState([]);
    const [loadingData, setLoadingData] = useState(false);

    useEffect(() => {
        fetchdata();
    }, []);


    const fetchdata = async () => {
        const response = await fetch("https://randomuser.me/api");
        const data = await response.json();
        console.log(data.results[0]);
        document.title = data.name.first;
        setData(data.results[0]);
        setLoadingData(true);
    };

    return (
        <div>
        {loadingData ? (
          <Space size={12}>
            <Avatar size={250} src={<Image src={data.picture.large}  style={{ width: 300 }} />} />
          </Space>
        ) : (
          <p>No data</p>
        )}
        <br/>
        <button onClick={() => fetchdata()}>Refresh Data <i className="fa fa-refresh"></i></button>
      </div>
    );
};

export default GetUserData;
