import 'antd/dist/antd.css';
import './index.css';
import { Image, Button, Space, Avatar } from 'antd';

import React, { useEffect, useState } from 'react';

const GetUserData = () => {
    const [data, setData] = useState([]);
    const [userName, setUserName] = useState([]);
    const [user, setUser] = useState([]);
    const [loadingData, setLoadingData] = useState(false);

    useEffect(() => {
        fetchdata();
    }, []);


    const fetchdata = async () => {
        const response = await fetch("https://randomuser.me/api");
        const data = await response.json();
        console.log(data.results[0]);
        setUserName(data.results[0].name.first);
        document.title = userName;
        setData(data.results[0]);
        setLoadingData(true);
    };

    return (
        <div>
        {loadingData ? (
          <Space size={12}>
            <Avatar size={250} src={<Image src={data.picture.large}  style={{ width: 300 }} />} />
          </Space>,
          <h4><b>{data.name.first} {data.name.last}</b></h4> 
        ) : (
          <p>No data</p>
        )}
        <br/>
        <button onClick={() => fetchdata()}>Refresh Data <i className="fa fa-refresh"></i></button>
      </div>
    );
};

export default GetUserData;
