import React, { useEffect, useState } from 'react';

import './body.css';


const GetData = () => {
  const [data, setData] = useState([]);
  const [user, setUser] = useState([]);
  const [loadingData, setLoadingData] = useState(false);

  useEffect(() => {
    fetchdata();
  }, []);

  const fetchdata = async () => {
    const response = await fetch("https://randomuser.me/api");
    const data = await response.json();
    console.log(data.results[0]);
    setData(data.results[0]);
    setLoadingData(true);
  };

  return (
    <div>
      {loadingData ? (
        <div className="card">
                <img src={data.picture.large}  alt="User" width="280" height="250"/>
                <div className="container">
                    <h4><b>{data.name.first} {data.name.last}</b></h4> 

                    <i class="fa fa-envelope" aria-hidden="true"><a href={"mailto:" + data.email}>{data.email}</a></i>
                    <p><i class="fa fa-map-marker"></i> {data.location.state}</p>
                </div>
        </div>
      ) : (
        <p>No user found, please try again</p>
      )}
      <br/>
      <button onClick={() => fetchdata()}>Refresh Data <i className="fa fa-refresh"></i></button>
    </div>
  );
};

export default GetData;
