import 'antd/dist/antd.css';
import './index.css';
import { Image, Button, Space, Avatar, Descriptions } from 'antd';

import React, { useEffect, useState } from 'react';

const GetUserData = () => {
    const [data, setData] = useState([]);
    const [user, setUser] = useState([]);
    const [loadingData, setLoadingData] = useState(false);

    useEffect(() => {
        fetchdata();
    }, []);


    const fetchdata = async () => {
        const response = await fetch("https://randomuser.me/api");
        const data = await response.json();
        console.log(data.results[0]);
        setData(data.results[0]);
        setLoadingData(true);
        title(data);
    };

    function title(title) {
        document.title = title.results[0].name.first;
    }


    return (
        <div>
        {loadingData ? (
          <Space size={12}>
            <Avatar size={250} src={<Image src={data.picture.large}  style={{ width: 300 }} />} />
            <h4><b>{data.name.first} {data.name.last}</b></h4> 
          </Space>,
          <Descriptions
            title="Responsive Descriptions"
            bordered
            column={{ xxl: 4, xl: 3, lg: 3, md: 3, sm: 2, xs: 1 }}
            >
            <Descriptions.Item label="Product">Cloud Database</Descriptions.Item>
            <Descriptions.Item label="Billing">Prepaid</Descriptions.Item>
            <Descriptions.Item label="time">18:00:00</Descriptions.Item>
            <Descriptions.Item label="Amount">$80.00</Descriptions.Item>
            <Descriptions.Item label="Discount">$20.00</Descriptions.Item>
            <Descriptions.Item label="Official">$60.00</Descriptions.Item>
            <Descriptions.Item label="Config Info">
                Data disk type: MongoDB
                <br />
                Database version: 3.4
                <br />
                Package: dds.mongo.mid
                <br />
                Storage space: 10 GB
                <br />
                Replication factor: 3
                <br />
                Region: East China 1
            </Descriptions.Item>
            </Descriptions>
        ) : (
          <p>No data</p>
        )}
        <br/>
        <button onClick={() => fetchdata()}>Refresh Data <i className="fa fa-refresh"></i></button>
      </div>
    );
};

export default GetUserData;
