import React, { useEffect, useState } from 'react';

import './body.css';


const GetData = () => {
  const [data, setData] = useState([]);
  const [user, setUser] = useState([]);
  const [loadingData, setLoadingData] = useState(false);

  useEffect(() => {
    fetchdata();
  }, []);

  const fetchdata = async () => {
    const response = await fetch("https://randomuser.me/api");
    const data = await response.json();
    console.log(data.results[0]);
    setData(data.results[0]);
    setLoadingData(true);
  };

  return (
    <div>
      {loadingData ? (
        <div>
            <div className="card">
                <img src={data.picture.large} alt="Avatar" style="width:100%"> </img>
                <div className="container">
                    <h4><b>{data.name.first} {data.name.last}</b></h4> 
                    <p>{data.email}</p> 
                </div>
            </div>
          <ul>
            <li><strong>First name:</strong> {data.name.first}</li>
            <li><strong>Last name:</strong> {data.name.last}</li>
            <li><strong>Email:</strong> {data.email}</li>
          </ul>
        </div>
      ) : (
        <p>No user found, please try again</p>
      )}
      <button onClick={() => fetchdata()}>Refresh Data</button>
    </div>
  );
};

export default GetData;
